mapper.select:
SELECT f.feed_id, f.contents, f.location, f.created_at, f.writer_user_id, u.uid AS writerUid ,u.nick_name AS writerNickName, u.pic AS writerPic , l.feed_id IS NOT NULL AS isLike
FROM feed AS f INNER JOIN user AS u ON u.user_id = f.writer_user_id
LEFT JOIN feed_like as l ON f.feed_id = l.feed_id and l.user_id = #{signedUserId} ORDER BY f.feed_id DESC LIMIT #{startIdx}, #{size}
